      barMinWidth: 50,
      barCategoryGap: '50px',
    }],
  };

  constructor() { }

  ngOnChanges(): void {
    this.setChartMergeOption();
  }

  setChartMergeOption(): void {

    const items = [
      {
        name: 'Raised',
        value: this.raised,
        color: '#289a58',
      },
      {
        name: 'Spent',
        value: this.spent,
        color: '#ff2c19',
      },
    ]; 

    this.mergeOption = {
      xAxis: {
        data: items.map( items => items.name ),
      },
      series: [{
