    tooltip: {
      show: true,
      trigger: 'axis',
      formatter: (params) => (params[0].data.value === null) ? '' : 
          `${params[0].data.name}:<br />$${params[0].data.value.toLocaleString()}`,
      axisPointer: {
        type: 'shadow',
      }
    },
    xAxis: {
      type: 'category',
      axisTick: {
        show: false
      },
      axisLabel: {
        interval: 0,
        formatter: (value) => `${value.split(' ').join('\n')}`,
      },
    },
    yAxis: {
      show: false,
    },
    series: [{
      type: 'bar',
      label: {
        show: true,
        position: 'top',
        fontSize: 18,
