import { MenuItem } from "primeng/api";
import { CandidateMenuItem } from "../lib-ui-components.models";

export class CandidateUtil {
  static addRouterLinks(candidates: CandidateMenuItem[], detailsActive: boolean): CandidateMenuItem[] {
    if (candidates?.length > 0) {
      return candidates.map(candidate => this.addRouterLink(candidate, detailsActive));
    } else {
      return candidates;
    }
  }

  static addRouterLink(candidate: CandidateMenuItem, detailsActive: boolean): CandidateMenuItem {
    let linkPrefix = `/year/${candidate.electionYear}/office/`;
    linkPrefix = linkPrefix + candidate.office.toLowerCase().split(' ').join('-');
    const linkDistrict =  candidate.district 
      ? `/${candidate.district}`
      : `/0`;
    const linkSuffix = detailsActive ? `/details` : ``;
    candidate.routerLink = `${linkPrefix}${linkDistrict}/${candidate.id}${linkSuffix}`;
    return candidate;
  }


  static getCandidateItems(candidates: CandidateMenuItem[]): MenuItem[] {
    return candidates.map(candidate => this.getCandidateItem(candidate))
  }

  static getCandidateItem(candidate: CandidateMenuItem): MenuItem {
    return {
      id: candidate.id,
      label: candidate.fullName,
      icon: candidate.inGeneralElection ? 'pi pi-check-circle' : 'pi pi-user',
      routerLink: candidate.routerLink,
      routerLinkActiveOptions: { exact:true },
    }
  }


  static getSeeAllCandidatesItem(officeTitle: string, year: string, district: string = null): MenuItem {
    let linkPrefix = `/year/${year}/office/`;

    linkPrefix = linkPrefix + `${officeTitle.toLowerCase().split(' ').join('-')}`;

    const linkDistrict =  district 
      ? `/${district}`
      : `/0`;

    return {
      label: 'See All ',
      icon: 'pi pi-users',
      routerLink: `${linkPrefix}${linkDistrict}`,
      routerLinkActiveOptions: { exact:true },     
    }
  }

  // Districts/Seats
  static hasSeats(candidates: CandidateMenuItem[]): boolean {
    return candidates.some(candidate => candidate?.district);
  }

  static getDistinctSeats(candidates: CandidateMenuItem[]): string[] {
    const seats: string[] = candidates.map(candidate => candidate.district).sort();
    return [... new Set(seats)];
  }
}

import { ActiveMenuPath, CandidateMenuItem } from "../lib-ui-components.models";
import { CandidateUtil } from "./candidate-util";

export class Office {
  id: string;
  title: string;
  label: string;
  icon: string;
  year: string;
  district: string = '';
  candidates: CandidateMenuItem[] = null;
  disabled: boolean = true;
  activeItem: ActiveMenuPath;

  constructor(title: string, icon: string, candidates: CandidateMenuItem[] = null) {
    this.id = title.toLowerCase().split(' ').join('-');
    this.title = title.toLowerCase().split(' ').join('-');
    this.label = title.toUpperCase();
    this.icon = icon;
    this.candidates = candidates;
  }

  setDisabled() {
    this.disabled = true;
  }

  setEnabled() {
    this.disabled = false;
  }

  setCandidates(candidates: CandidateMenuItem[]) {
    this.candidates = candidates;
  }

  addRouterLinks(detailsActive: boolean) {
    this.candidates = CandidateUtil.addRouterLinks(this.candidates, detailsActive);
  }

  getItems() {
    const hasSeats = CandidateUtil.hasSeats(this.candidates);
    if (hasSeats) {
      const distinctSeats = CandidateUtil.getDistinctSeats(this.candidates);

      return distinctSeats.map(seat => {
        const seeAllCandidatesItem = CandidateUtil.getSeeAllCandidatesItem(this.title, this.year, seat);

        const candidatesMenuItems = this.candidates
          .filter(candidate => candidate.district === seat)
          .map(candidate => CandidateUtil.getCandidateItem(candidate));

        return {
          id: seat,
          label: `District ${seat}`,
          icon: 'fa fa-fw fa-map-marker',
          items: [ seeAllCandidatesItem, ...candidatesMenuItems, ],
          expanded: 
            this.activeItem?.districtNumber?.toUpperCase() === seat.toUpperCase(),
        }
      });

    } else {
      const seeAllCandidatesItem = CandidateUtil.getSeeAllCandidatesItem(this.title, this.year, this.district)

      const candidatesMenuItems = CandidateUtil.getCandidateItems(this.candidates);

      return [ seeAllCandidatesItem, ...candidatesMenuItems, ];
    }
  }

  toMenuItem(activeItem?: ActiveMenuPath) {
    this.activeItem = activeItem;

    const subItems = this.candidates && !this.disabled 
      ? this.getItems()
      : null;

    return {
      id: this.id,
      label: this.label,
      disabled: this.disabled,
      items: subItems,
      expanded: 
        activeItem?.officeTitle?.toUpperCase() === this.title?.toUpperCase(),
    }
  }
};

