styleUrlsData.data:
.details-header-wrapper {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr; 
  grid-template-rows: 1fr 1fr 1fr;
  column-gap: 10px;
  align-items: stretch;
  background-color: #ECEFF2;

  div {
    background-color: white;
  }
}

.image {
  grid-column: 1;
  grid-row: 1 / 4;

  padding: 5px;

  display: flex;
  flex-direction: column;
  justify-content: space-around;

  background-color: white;
}

.image img {
  border-radius: 50%;
  max-width: 100%;
  max-height: 100%;
  background-color: rgb(238, 238, 238);
}

.info {
  grid-column: 2;
  grid-row: 1 / 4;

  text-align: center;
  font-size: clamp(1em, 1.75vw, 10vw);

  padding-left: 10px;
  padding-right: 10px;

  display: flex;
  flex-direction: column;
  justify-content: space-around;

  .website fa-icon {
    padding-right: MIN(.6em, 2vw);
  }
}

.candidate-name {
  font-weight: bold;
  font-size: clamp(1.5em, 2.5vw, 10vw);
}

.raised, .donors, .average-donation {
  padding-left: 10px;
  padding-right: 10px;

  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;

  font-size: clamp(1em, 2.5vw, 10vw);
  .amount {
    font-size: 1.25em;
  }  
}

.raised, .donors, .average-donation {
  grid-column: 3;
}
.raised {
  grid-row: 1;
}
.donors {
  grid-row: 2;
}
.average-donation {
  grid-row: 3;
}

.image-info > .image, .image-info > .info {
  display: none;
}

@media (min-width: 768px) {
  .details-header-wrapper {
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr;
    column-gap: 15px;    
  }
  .image, .info, .average-donation {
    grid-row: 1 / 3;
  }
  .info {
    padding-left: 15px;
    padding-right: 15px;
  }

  .raised, .donors, .average-donation {
    flex-direction: column;
    justify-content: center;
    text-align: center;
  }

  .average-donation {
    grid-column: 4;
    div {
      padding: 10px;
    }
  }

  .raised, .donors {
    grid-column: 3;
    div {
      padding: 5px;
    }
  }
}

@media (min-width: 1500px) {
  .details-header-wrapper {
    grid-template-rows: 1fr 1fr;
  }

  .details-header-wrapper > .image, .details-header-wrapper > .info {
    display: none;
  }
  .image-info > .image, .image-info > .info {
    display: unset;
  }

  .image-info {
    grid-row: 1 / 3;
    display: flex;
    flex-direction: column;
    
    .image {
      display: flex;
      flex-direction: row;
      justify-content: center;
  
      img {
        max-width: 40%;
      }
    }

    .info {
      justify-content: space-evenly;
    }
  }


  .raised, .donors, .average-donation {
    grid-row: 1 / 3;
  }
  .raised {
    grid-column: 2;
  }
  .donors {
    grid-column: 3;
  }
  .average-donation {
    grid-column: 4;
  }
}

@media (max-width:380px) {
  .details-header-wrapper {
    min-height: 120px;
    grid-template-columns: 1fr 1fr;
  }

  .image {
    display: none;
  }

  .info {
    grid-column: 1;
  }
  
  .raised, .donors, .average-donation {
    grid-column: 2;
  }
}

@media (max-width:300px) {
  .details-header-wrapper {
    grid-template-columns: 1fr;
  }
  .raised, .donors, .average-donation {
    display: none;
  }
}

