export class RaisedVsSpentBarGQLComponent implements OnChanges {
  @Input() candidateId: string;

  raised = 0;
  spent = 0;
  contributionsColor = globals.contributionsColor;
  expendituresInSupportColor = globals.expendituresInSupportColor;

