import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { ActiveMenuPath } from 'lib-ui-components';
import { CandidateMenuGQL, CandidateMenuResponse } from './candidate-menu-gql.query';

export { ActiveMenuPath as ActiveMenuPathGQL };
@Component({
  selector: 'gql-candidate-menu',
  template: `
    <candidate-menu 
      [activeItem]="activeItem"
      [detailsActive]="detailsActive"
      [mayor]="mayor"
      [cityCouncil]="cityCouncil"
      [cityAttorney]="cityAttorney"
    ></candidate-menu>
  `,
})
export class CandidateMenuGQLComponent implements OnChanges {
  @Input() electionYear: string;

  @Input() activeItem: ActiveMenuPath;
  @Input() detailsActive: boolean;

  mayor;
  cityCouncil;
  cityAttorney;

  constructor(private candidateMenuGQL: CandidateMenuGQL) {}

  ngOnChanges(changes: SimpleChanges) {
    if (changes['electionYear']) {
      const electionYear = changes['electionYear'].currentValue;
      this.electionYearChanged(electionYear);
    }

  }

  electionYearChanged(electionYear) {
    this.electionYear = electionYear;

    if (!this.electionYear) {
      this.mayor = null;
      this.cityCouncil = null;
      this.cityAttorney = null;
      return; 
    }

    this.candidateMenuGQL.watch({
      year: this.electionYear,
    }, {
      // errorPolicy: 'all',
    }).valueChanges.subscribe( (result: any) => {

      if (!result?.data?.electionYear) { return; }

      const response: CandidateMenuResponse = JSON.parse(JSON.stringify(result.data.electionYear));

      this.mayor = response.officesByType.mayor;
      this.cityCouncil = response.officesByType.cityCouncil;
      this.cityAttorney = response.officesByType.cityAttorney;
    });
  }
}

import { Component, OnInit } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';
import { filter } from 'rxjs/operators';
import { ActiveMenuPathGQL } from 'src/app/graphql/candidate-menu-gql/candidate-menu-gql.component';
import { YearService } from 'src/app/store/services/year.service';

@Component({
  selector: 'candidate-menu-routed',
  template: `
    <gql-candidate-menu
      [electionYear]="activeYear"
      [activeItem]="activeItem"
      [detailsActive]="detailsActive"
    ></gql-candidate-menu>
  `,
})
export class CandidateMenuRoutedComponent implements OnInit {
  activeYear: string;
  activeItem: ActiveMenuPathGQL = {
    officeTitle: '',
    districtNumber: '',
    candidateId: '',
  };
  detailsActive: boolean = false;

  constructor(
    private router: Router,
    private yearService: YearService,
  ) { }

  ngOnInit() {
    this.yearService.electionYearChanged$.subscribe(year => {
      this.activeYear = year;
    })

    this.trackDetailsActive();
  }

  getActiveItem(fragments: string[]) {
    let path: ActiveMenuPathGQL = {
      officeTitle: '',
      districtNumber: '',
      candidateId: '',
    };

    let fragmentIndex = fragments.indexOf('office');
    if (fragmentIndex >= 0 ) {
      path.officeTitle = fragments[++fragmentIndex];
      path.districtNumber = fragments[++fragmentIndex];
    }

    return path;
  }

  trackDetailsActive() {
    this.router.events
    .pipe(filter(event => event instanceof NavigationEnd))
    .subscribe(event => {
      const urlFragments = event['url'].split('/').filter( fragment => fragment);
      this.activeItem = this.getActiveItem(urlFragments);

      const detailsActive = event['url'].includes('/details')
      this.detailsActive = detailsActive;
    });
  }
}

