import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { RaisedInVsOutGQL, RaisedInVsOut } from './raised-in-vs-out-gql.query';

@Component({
  selector: 'gql-in-vs-out-donut',
  template: `
    <raised-in-vs-out-donut
      [inside]="insideAmount"
      [outside]="outsideAmount"
    ></raised-in-vs-out-donut>
  `,
})
export class RaisedInVsOutDonutGQLComponent implements OnChanges {
  @Input() candidateId: string;

  insideAmount = 0;
  outsideAmount = 0;

  constructor(private raisedInVsOutGQL: RaisedInVsOutGQL) {}

  ngOnChanges(changes: SimpleChanges): void  {
    if (changes['candidateId']) {
      const candidateId = changes['candidateId'].currentValue;
      this.update(candidateId);
    }
  }

  update(candidateId: string) {
    this.candidateId = candidateId;

    if (!this.candidateId) { return; }

    this.raisedInVsOutGQL.watch({
      candidateId: this.candidateId,
    }, {
      // errorPolicy: 'all',
    }).valueChanges.subscribe( (result: any) => {
      const response: RaisedInVsOut = result.data;

      const insideAmount = response?.candidate?.committee?.contributions?.categorizedBy?.jurisdiction?.inside;
      this.insideAmount = insideAmount ? insideAmount : 0;

      const outsideAmount = response?.candidate?.committee?.contributions?.categorizedBy?.jurisdiction?.outside;
      this.outsideAmount = outsideAmount ? outsideAmount : 0;
    });
  }
}

import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { CandidateInfoGQL, CandidateInfo } from './candidate-info-gql.query';

@Component({
  selector: 'gql-raised-in-vs-out-title',
  template: `
    <chart-title class="chart-title"
      [titleText]="title"
      [tooltipText]="tooltip"
    ></chart-title>
  `,
})
export class RaisedInVsOutTitleGQLComponent implements OnChanges {
  @Input() candidateId: string;

  title = '';
  tooltip = '';

  constructor(private candidateInfoGQL: CandidateInfoGQL) {}

  ngOnChanges(changes: SimpleChanges): void  {
    if (changes['candidateId']) {
      const candidateId = changes['candidateId'].currentValue;
      this.update(candidateId);
    }
  }

  update(candidateId: string) {
    this.candidateId = candidateId;

    if (!this.candidateId) { return; }

    this.candidateInfoGQL.watch({
      candidateId: this.candidateId,
    }, {
      // errorPolicy: 'all',
    }).valueChanges.subscribe( (result: any) => {
      const response: CandidateInfo = result.data;

      const district = response?.candidate?.district;
      const jurisdictionComplete = district ? `District ${district}` : 'City';

      this.title = `In vs. Out of ${jurisdictionComplete}`;

      const agency = response?.candidate?.agency;
      const area = district ? `${agency}, District ${district}` : `${agency}`;

      this.tooltip = `Total campaign funds raised inside versus funds raised outside ${area}.`;
    });
  }
}

