import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { IndependentExpendituresGQL, IndependentExpenditures } from '../independent-expenditures-gql.query';

@Component({
  selector: 'gql-support-oppose-bar',
  template: `
    <support-oppose-bar
      [support]="supportAmount"
      [oppose]="opposeAmount"
      [backgroundColor]="backgroundColor"
      [textColor]="textColor"
    ></support-oppose-bar>
  `,
})
export class SupportOpposeBarGQLComponent implements OnChanges {
  @Input() candidateId: string;
  @Input() backgroundColor: string;
  @Input() textColor: string;

  supportAmount = 0;
  opposeAmount = 0;

  constructor(private independentExpendituresGQL: IndependentExpendituresGQL) {}

  ngOnChanges(changes: SimpleChanges): void  {
    if (changes['candidateId']) {
      const candidateId = changes['candidateId'].currentValue;
      this.update(candidateId);
    }
  }

  update(candidateId: string) {
    this.candidateId = candidateId;

    if (!this.candidateId) { return; }

    this.independentExpendituresGQL.watch({
      candidateId: this.candidateId,
    }, {
      // errorPolicy: 'all',
    }).valueChanges.subscribe( (result: any) => {
      const response: IndependentExpenditures = result.data;

      const supportAmount = response?.candidate?.independentExpenditures?.sums?.support;
      this.supportAmount = supportAmount ? supportAmount : 0;
      
      const opposeAmount = response?.candidate?.independentExpenditures?.sums?.oppose;
      this.opposeAmount = opposeAmount ? opposeAmount : 0;
    });
  }
}

import { Component, Input, OnChanges, Output, EventEmitter, OnInit} from '@angular/core';

import { getCompactFormattedCurrency } from '../../number-formatter'

export interface Category {
  id: string;
  name: string;
  value: number;
  percent: number;
  color: string;
}

@Component({
  selector: 'app-top-categories-table',
  templateUrl: './top-categories-table.component.html',
  styleUrls: ['./top-categories-table.component.scss']
})
export class TopCategoriesTableComponent implements OnChanges, OnInit {

  @Input() categories: Category[];
  @Input() categoryHighlighted: string;
  @Output() categoryHighlightedChange = new EventEmitter<string>();
    
  dataSource;
  displayedColumns: string[] = ['name', 'value', 'percent'];

  constructor() { }
  ngOnInit(): void {
    let categories = this.categories;
    this.setTabletData(categories);
  }

  ngOnChanges(): void {
    let categories = this.categories;
    this.setTabletData(categories);
  }

  setTabletData(tableData: object[]): void {

    this.dataSource = tableData.map( (row) => ({
        ...row,
        value: getCompactFormattedCurrency(row['value']),
        boxShadowStyle: `10px 0px 0px 0px ${row['color']} inset`,
        percent: (row['percent']/100)
          .toLocaleString("en", { style: "percent", minimumFractionDigits: 1 }),
      })
    );

  }

  hoveredRow(row: object) {  
    this.categoryHighlightedChange.emit(row['id']);
  }

}

